#if 0
//----------------------------------------------------------------------
//  Brief   : Read pattern gen status
//  @param  : ECS -> path(main:0/pip:1)
//  @return : ECS <- data(0:off/1:on)
// 
//----------------------------------------------------------------------
UINT16 d_sony_interface_read_PTGEN(BOOL path)
{
	if (path == MAIN_PATH)
	{
		return RegReadFldAlign(OUTSTG_OS_28, OS_28_MAIN_PTGEN_EN);
	}
	else /* SUB_PATH */
	{
		return RegReadFldAlign(OUTSTG_OS_28, OS_28_PIP_PTGEN_EN);
	}
}


//----------------------------------------------------------------------
//  Brief   : Set pattern gen
//  @param  : ECS -> path(main:0/pip:1), data(0:off/1:on)
//  @return : none
// 
//----------------------------------------------------------------------
void d_sony_interface_write_PTGEN(BOOL path, UINT16 write_data)
{
	write_data = write_data & b0;

	if (path == MAIN_PATH)
	{
		vDrvSetOSTGInPattern((UINT32)MAIN_PATH, (UINT32)write_data);
	}
	else /* SV_VP_PIP */
	{
		vDrvSetOSTGInPattern((UINT32)SUB_PATH, (UINT32)write_data);
	}
}
#endif





