extern unsigned long get_drvmem_mem(void);

static BOOL fgHugeMemAlloc = FALSE;

void  imgreszvfy_alloc_mem(BOOL fgFrtTime)
{
    UINT32 Alloc_Addr ;
    if(FALSE == fgHugeMemAlloc)
    {
        //Alloc_Addr = (UINT32)get_drvmem_mem();
        Alloc_Addr = (UINT32)BSP_AllocVfyReserveMemory(0, 2048, 160*1024*1024);
        UTIL_Printf("<Imgresz> alloc base pa 0x%x\n", Alloc_Addr);
        Alloc_Addr = VIRTUAL(Alloc_Addr);
        u4_All_Alloc_BaseSA = Alloc_Addr;
        u4AllocBaseAddr = (u4_All_Alloc_BaseSA);
        u4DrvAllocBaseAddr = u4AllocBaseAddr;
        UTIL_Printf("<Imgresz>Alloc all base SA is 0x%x--0x%x.....\n",
                    Alloc_Addr, Alloc_Addr + 160*1024*1024 );
        fgHugeMemAlloc = TRUE;
    }
    else
    {
        u4AllocBaseAddr = (u4_All_Alloc_BaseSA);
        u4DrvAllocBaseAddr = u4AllocBaseAddr;
        UTIL_Printf("<Imgresz>Get Init Base SA is 0x%x.....\n", u4_All_Alloc_BaseSA);
    }
}

UINT32  imgreszvfy_alloc_mem_aligned_vfy(UINT32 Buffsize, UINT32 align)
{
    UINT32 Alloc_Addr ;
    Alloc_Addr =((u4AllocBaseAddr +align-1)/align)*align;
    u4AllocBaseAddr = (Alloc_Addr+Buffsize);
    // x_memset(Alloc_Addr, 0, Buffsize ); //for clip
    u4DrvAllocBaseAddr = u4AllocBaseAddr;
    UTIL_Printf("<Imgresz>Alloc1 Addr is 0x%x, sz 0x%x.NewBase 0x%x \n",Alloc_Addr, Buffsize, u4DrvAllocBaseAddr);
    return Alloc_Addr;
}


UINT32 imgreszvfy_alloc_mem_aligned_vfy_later(UINT32 Buffsize, UINT32 align)

{
    UINT32 Alloc_Addr ;
    if((u4AllocBaseAddr-u4DrvAllocBaseAddr)>100*1024*1024)
    {
        UTIL_Printf("<Imgresz>sync to start.....\n");
        u4AllocBaseAddr = u4DrvAllocBaseAddr   ;
    }

    Alloc_Addr = ((u4AllocBaseAddr +align-1)/align)*align;

    //x_memset(Alloc_Addr, 0, Buffsize );
    u4AllocBaseAddr = (Alloc_Addr+Buffsize);

    UTIL_Printf("<Imgresz>Alloc2 Addr is 0x%x, sz 0x%x(0x%x).NewBase 0x%x \n",
       Alloc_Addr, Buffsize, u4AllocBaseAddr-u4DrvAllocBaseAddr, u4DrvAllocBaseAddr);

    return Alloc_Addr;
}

