INT32 i4ImgReszStrToInt(UCHAR *ucStr)
{
    UINT32 u4I = 0;
    INT32 i4Value = 0;

    if(ucStr == 0)
        return 0;

    while((*(ucStr + u4I) >= '0') && (*(ucStr + u4I) <= '9'))
    {
        i4Value *= 10;
        i4Value += (INT32)(*(ucStr + u4I) - '0');
        u4I++;
    }

    return i4Value;
}


