static UINT32 i4ImgreszVfyGoldenPath(BYTE* pGoldFileName, BYTE* pFileName)
{
    char bfile[50]={0};

    i4ImgReszVfyGetFileName(bfile, pFileName);
    i4ImgReszVfyGetPath(pGoldFileName, pFileName);

    strcat(pGoldFileName, "golden\\");
    strcat(pGoldFileName, bfile);

    return 0;
}

//0: ok
//-1 : ng
static UINT32 i4ImgreszCompare(UINT32 u4Buf1, UINT32 u4Buf2, UINT32 u4Buflen)
{
    UINT32 i4Ret= 0;
    UINT32 i4len = 0;
    while(i4len<u4Buflen/4)
    {
        if(*(UINT32*)u4Buf1 != *(UINT32*)u4Buf2)
        {
            i4ImgReszVfyPrintf("<imgresz>CMP fail %d 0x%x 0x%x \n", i4len*4,*(UINT32*)u4Buf1,*(UINT32*)u4Buf2);
            i4Ret = -1;
            break;
        }
        i4len++;
        u4Buf1 += 4;
        u4Buf2 += 4;
    }
    return i4Ret;
}

/* 0 : compare ok
   -1 : compare fail
   -2 : no golden data
*/
static INT32 i4ImgreszVfyCompareGolden(BYTE* pbFileName, UINT32 u4Num,
                                       IMGRESZ_HAL_IMG_INFO_T *prDestImgInfo)
{
    INT32 i4Ret = 0;
    char bFileName[256]={0};
    char bGoldFileName[256]={0};
    INT32 i4FileHandle;
    UINT32 u4Size = 0;
    UINT32 u4BufSA1, u4BufSA2, u4BufSA3;
    INT32 i4FileRet =0;

    i4ImgReszVfyPrintf("<imgresz>begin compare golden %s \n", pbFileName);

    switch(prDestImgInfo->rBufferFormat.eBufferMainFormat)
    {
        case IMGRESZ_HAL_IMG_BUF_MAIN_FORMAT_Y_C_BUFFER:
            {
                sprintf(bFileName,"%s.%04d.frame.y.bin",(char *)pbFileName,(int)u4Num);
                i4ImgreszVfyGoldenPath(bGoldFileName, bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ> YC Y File : %s \n", bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ> YC Y Golden File : %s \n", bGoldFileName);

                u4BufSA1 = imgreszvfy_alloc_mem_aligned_vfy_later(
                              prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight, 1);

                x_memset(u4BufSA1, 0, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);

                i4FileRet = i4ImgReszVfyReadFile(bGoldFileName,(UINT32)u4BufSA1,
                         prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                x_thread_delay(50);
                if(i4FileRet > 0)
                {
                   i4ImgReszVfyPrintf("<imgresz>CMP golden Y suc 0x%x 0x%x To Cmp\n", i4FileRet, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                   i4Ret = i4ImgreszCompare(u4BufSA1, prDestImgInfo->u4BufSA1,
                             prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                   if(-1 == i4Ret)
                    break;
                }
                else
                {
                    i4ImgReszVfyPrintf("<imgresz>Read golden Y fail\n");
                    i4Ret = -2;
                    break;
                }

                x_memset(bGoldFileName, 0, sizeof(bGoldFileName));
                x_memset(u4BufSA1, 0, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);

                sprintf(bFileName,"%s.%04d.frame.c.bin",(char *)pbFileName,(int)u4Num);
                i4ImgreszVfyGoldenPath(bGoldFileName, bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ> YC C File : %s \n", bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ> YC C Golden File : %s \n", bGoldFileName);
                switch(prDestImgInfo->rBufferFormat.eYUVFormat)
                {
                    case IMGRESZ_HAL_IMG_YUV_FORMAT_420:
                        u4Size = (prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight)/2;
                        break;
                    case IMGRESZ_HAL_IMG_YUV_FORMAT_422:
                        u4Size = (prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                        break;
    			    default:
    				    break;
                }
                i4FileRet = i4ImgReszVfyReadFile(bGoldFileName,(UINT32)u4BufSA1,
                                     u4Size);
                x_thread_delay(50);
                if(i4FileRet > 0)
                {
                   i4ImgReszVfyPrintf("<imgresz>CMP golden C suc 0x%x 0x%x To Cmp\n", i4FileRet, u4Size);
                   i4Ret = i4ImgreszCompare(u4BufSA1, prDestImgInfo->u4BufSA2,
                             u4Size);
                }
                else
                {
                    i4ImgReszVfyPrintf("<imgresz>Read golden C fail\n");
                    i4Ret = -2;
                    break;
                }

                imgreszvfy_free_mem_aligned_vfy(u4BufSA1);
            }
            break;
        case IMGRESZ_HAL_IMG_BUF_MAIN_FORMAT_ARGB_BUFFER:
            {
                sprintf(bFileName,"%s.%04d.argb.bin",(char *)pbFileName,(int)u4Num);
                i4ImgreszVfyGoldenPath(bGoldFileName, bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ>ARGB File : %s \n", bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ>ARGB Golden File : %s \n", bGoldFileName);

                u4BufSA1 = imgreszvfy_alloc_mem_aligned_vfy_later(
                              prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight, 1);

                x_memset(u4BufSA1, 0, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);

                i4FileRet = i4ImgReszVfyReadFile(bGoldFileName,(UINT32)u4BufSA1,
                         prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                x_thread_delay(50);
                if(i4FileRet > 0)
                {
                   i4ImgReszVfyPrintf("Read golden suc 0x%x 0x%x To Cmp\n", i4FileRet, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                   i4Ret = i4ImgreszCompare(u4BufSA1, prDestImgInfo->u4BufSA1,
                             prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                }
                else
                {
                    i4ImgReszVfyPrintf("Read golden fail\n");
                    i4Ret = -2;
                }

                imgreszvfy_free_mem_aligned_vfy(u4BufSA1);
           }
            break;

          case IMGRESZ_HAL_IMG_BUF_MAIN_FORMAT_AYUV_BUFFER:
            {
                sprintf(bFileName,"%s.%04d.ayuv.bin",(char *)pbFileName,(int)u4Num);
                i4ImgreszVfyGoldenPath(bGoldFileName, bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ>AYUV File : %s \n", bFileName);
                i4ImgReszVfyPrintf("<IMGRESZ>AYUV Golden File : %s \n", bGoldFileName);

                u4BufSA1 = imgreszvfy_alloc_mem_aligned_vfy_later(
                              prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight, 1);

                x_memset(u4BufSA1, 0, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);

                i4FileRet = i4ImgReszVfyReadFile(bGoldFileName,(UINT32)u4BufSA1,
                         prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);

                x_thread_delay(50);

                if(i4FileRet > 0)
                {
                   i4ImgReszVfyPrintf("Read golden suc 0x%x 0x%x To Cmp\n", i4FileRet, prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                   i4Ret = i4ImgreszCompare(u4BufSA1, prDestImgInfo->u4BufSA1,
                             prDestImgInfo->u4BufWidth*prDestImgInfo->u4BufHeight);
                }
                else
                {
                    i4ImgReszVfyPrintf("Read golden fail\n");
                    i4Ret = -2;
                }
                imgreszvfy_free_mem_aligned_vfy(u4BufSA1);
          }

        default:
            break;
    }

 return i4Ret;

}


