static INT32 i4ImgreszVfyGetTestCase(CHAR* pOrgStr, INT32 *u4CaseNum,
                                     CHAR* pPath, INT32 u4PathLen)
{
    UINT32 i4Case=0;
    UINT32 i = 0;
	char *p = strstr(pOrgStr, ":");
	int u4len = strlen(p) ;

    if(!pPath)
        return 0;

	u4len = u4len>u4PathLen ? u4len : strlen(p);

    while(pOrgStr[i] != ':')
    {
        i4Case = (pOrgStr[i]-'0')+i4Case*10;
		i++;
    }

    strncpy(pPath, p+1, u4len-1);

    *u4CaseNum = i4Case;

    return 0;
}




