                    x_memcpy(&rSrcImgInfoNext,&rSrcImgInfo,sizeof(IMGRESZ_HAL_IMG_INFO_T));

                    // Update next destination buffer info
                    x_memcpy(&rDestImgInfoNext,&rDestImgInfo,sizeof(IMGRESZ_HAL_IMG_INFO_T));
