                //if((rSrcImgInfoNext.u4ImgXOff != rSrcImgInfo.u4ImgXOff) ||
                //   (rSrcImgInfoNext.u4ImgYOff != rSrcImgInfo.u4ImgYOff))
                //    fgSourceOffsetChange = TRUE;

