            // Copy source fit buffer to source buffer
            u4BufSize = rSrcImgInfo.u4BufWidth * rSrcImgInfo.u4BufHeight;
            u4BufSize = ((u4BufSize + 1023) / 1024) * 1024; // Align to 1024 for address swap
