    IMGRESZ_HAL_IMG_INFO_T rSrcFitImgInfo;
    IMGRESZ_HAL_IMG_INFO_T rSrcImgInfo;
    IMGRESZ_HAL_IMG_INFO_T rDestImgInfo;
    IMGRESZ_HAL_IMG_INFO_T rDestFitImgInfo;
