void vImgReszVerifyTest(UINT32 u4TestCase)
{
    UINT32 u4FileListBufSize = _u4ImgReszFileListMax*256;
    BYTE **ppbFileList;
    UINT32 u4FileListSA;
    UINT32 u4CurFileListNum = 0;
    BYTE *pFileListPath;
    IMGRESZ_CASE *pImgreszCase = NULL;
    UINT32 u4FileListNum = 0;
    UINT32 u4CaseIndex = 0;

    pImgreszCase = x_alloc_aligned_dma_mem(sizeof(IMGRESZ_CASE)*_u4ImgReszFileListMax,1);
    VERIFY(pImgreszCase != 0);
    // Load file list
    u4FileListSA = (UINT32)x_alloc_aligned_dma_mem(u4FileListBufSize,1);
    VERIFY(u4FileListSA != 0);
    x_memset((void *)u4FileListSA, 0, u4FileListBufSize);
    ppbFileList = (BYTE **)x_alloc_aligned_dma_mem(_u4ImgReszFileListMax*sizeof(BYTE *),sizeof(BYTE *));
    VERIFY(ppbFileList != 0);

    pFileListPath = (BYTE*)x_alloc_aligned_dma_mem(u4FileListBufSize,1);
    VERIFY(pFileListPath != 0);
    x_memset((void *)pFileListPath, 0, u4FileListBufSize);

     //==============================================================
     /*                     Read File                              */
     //==============================================================
    IMGRZ_LOAD_FILE_LIST_FUNCTION;

    i4ImgReszVfyPrintf("[ImgResz][Verify] Load File Num %d\n", u4FileListNum);

    for(u4CurFileListNum=0;
        u4CurFileListNum<u4FileListNum
           && u4FileListNum <_u4ImgReszFileListMax;
       u4CurFileListNum++)
    {
        IMGRESZ_CASE *pTmpCase = pImgreszCase+u4CurFileListNum;
        i4ImgreszVfyGetTestCase(ppbFileList[u4CurFileListNum],
                               &u4CaseIndex,
                               pFileListPath+u4CurFileListNum*256, 256);

        pTmpCase->i4CaseNum = u4CaseIndex;
        pTmpCase->pFilePath = (BYTE*)(pFileListPath+u4CurFileListNum*256);

        i4ImgReszVfyPrintf("[ImgResz][Verify]Vfy File[%d] Path %s\n",
              u4CaseIndex, (CHAR*)(pFileListPath+u4CurFileListNum*256));

     }

#ifdef IMGRESZ_DRAM_SETTING_RESET
     vHwImgReszResetDramSetting(0);
#endif

#if IMGRESZ_ONE_HUGE_MEM
     imgreszvfy_alloc_mem(TRUE);     //alloc mem
#endif

     //==============================================================
     /*                     VFY                                    */
     //==============================================================
     for(u4CurFileListNum=0;
       u4CurFileListNum<u4FileListNum && u4FileListNum <_u4ImgReszFileListMax;
       u4CurFileListNum++)
    {
        IMGRESZ_CASE *pTmpCase = pImgreszCase+u4CurFileListNum;

#if IMGRESZ_ONE_HUGE_MEM
        imgreszvfy_alloc_mem(FALSE);   //init mem
#endif


        i4ImgReszVfyPrintf("<Imgresz> Begin to test case %d\n", pTmpCase->i4CaseNum);

        vImgreszVfyTestCase(pTmpCase);

        x_thread_delay(1000);
        i4ImgReszVfyPrintf("<Imgresz> End case %d\n", pTmpCase->i4CaseNum);
        i4ImgReszVfyPrintf("<Imgresz> ============================================\n");
        i4ImgReszVfyPrintf("<Imgresz> ********************************************\n");
        i4ImgReszVfyPrintf("<Imgresz> ============================================\n");
     }


    imgreszvfy_free_mem_aligned(pImgreszCase);
    imgreszvfy_free_mem_aligned(pFileListPath);
    imgreszvfy_free_mem_aligned(ppbFileList);
    imgreszvfy_free_mem_aligned(u4FileListSA);

    return ;

}


